sp_widget.client_script:
(function () {
    // Function to submit equipment request (replace with actual functionality)
    function submitRequest() {
        var equipmentType = document.getElementById('equipmentType').value;
        var quantity = document.getElementById('quantity').value;

        // Implement logic to submit the request to the backend (e.g., via ServiceNow REST API)
        // Update request history dynamically
        var requestHistory = document.getElementById('requestHistory');
        var newRequest = document.createElement('p');
        newRequest.textContent = 'Request: ' + quantity + ' ' + equipmentType + ' - Pending';
        requestHistory.appendChild(newRequest);
    }
})();

