sp_widget.client_script:
(function () {
    // Retrieve new hire information (replace with actual data)
    var employeeName = "John Doe";
    var onboardingTasks = ["Complete paperwork", "Attend orientation", "Set up workstation"];

    // Dynamically update widget content
    document.getElementById('employeeName').innerHTML = employeeName;
    document.getElementById('greeting').innerHTML = getGreeting();
    updateOnboardingChecklist();

    // Function to get dynamic greeting based on the time of day
    function getGreeting() {
        var hour = new Date().getHours();
        if (hour < 12) return "Good morning!";
        else if (hour < 18) return "Good afternoon!";
        else return "Good evening!";
    }

    // Function to update the onboarding checklist
    function updateOnboardingChecklist() {
        var checklist = document.getElementById('onboardingChecklist');
        onboardingTasks.forEach(function(task) {
            var listItem = document.createElement('li');
            listItem.textContent = task;
            checklist.appendChild(listItem);
        });
    }
})();

